package com.syntax.class01;

public class PrimitaveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// 1 create a variable=box=container
		
		/*
		 * to create a variable we need:
		 *   1. specify dataType
		 *   2. give a name
		 */
		
		//datatypes to represent numeric values 
		
		byte box1=127;  
		
		short box2=5896;
		
		int box3=327677666;//most used                            //onemli
		
		long box4=5324686794165861l; //will be using 
		
		//datatype to represent decimals
		
		float price=12.99F;
		
		
		double price1=13.99; //most used                     //onemi
		
		//to represent a single character 
		
		char gender='F';                                         //onemli
		
		char sign='$';
		
		//to represent yes or no
		
		boolean snow=false;                                          //onemli
		boolean tired=true;
		
		//I want to print 13.99??
		
		System.out.println(price1);
		
		System.out.println("price1");
		System.out.println(sign);
		System.out.println(gender);
		System.out.println("boolean "+snow);
		System.out.println(tired);
		System.out.println(box1);
		System.out.println(box2);
		System.out.println(box3);
		System.out.println(box4);
		System.out.println(price);
		
		
		
		
	}

}
